reporting.reportgraph.GraphQuery:
EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) COLLATE database_default );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day');
INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name)
SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Day') AS Dates LEFT JOIN ( SELECT HitsStartTime AS Interval, SUM(HitsValue) AS Hits ,OM_MVTest.MVTestDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_Statistics.StatisticsID = Analytics_DayHits.HitsStatisticsID INNER JOIN OM_MVTest ON OM_MVTest.MVTestName = SUBSTRING(StatisticsCode, 15, CHARINDEX(';',StatisticsCode,15)-15) AND MVTestSiteID = StatisticsSiteID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID ) AND StatisticsCode LIKE 'mvtconversion;%' AND (@MVTestName IS NULL OR @MVTestName = '' OR @MVTestName = OM_MVTest.MVTestName) AND (@ConversionName IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,OM_MVTest.MVTestDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'day' EXEC Proc_Analytics_RemoveTempTable
