reporting.reporttable.TableQuery:
SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'hour'); SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'hour'); DECLARE @myselection TABLE ( DocumentNamePath varchar(400), StatisticsObjectCulture varchar(400), Count float );
DECLARE @countedAll TABLE ( Counted float );
INSERT INTO @myselection SELECT TOP 100 CASE WHEN DocumentNamePath LIKE '' OR DocumentNamePath IS NULL THEN StatisticsObjectName ELSE DocumentNamePath END, StatisticsObjectCulture, SUM(HitsCount) FROM Analytics_Statistics INNER JOIN Analytics_HourHits ON Analytics_HourHits.HitsStatisticsID = Analytics_Statistics.StatisticsID LEFT JOIN View_CMS_Tree_Joined ON View_CMS_Tree_Joined.NodeID = Analytics_Statistics.StatisticsObjectID AND View_CMS_Tree_Joined.DocumentCulture = Analytics_Statistics.StatisticsObjectCulture WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) GROUP BY StatisticsObjectCulture, DocumentNamePath, StatisticsObjectName ORDER BY SUM(HitsCount) DESC, StatisticsObjectCulture
INSERT INTO @countedAll SELECT SUM(HitsCount) FROM Analytics_Statistics INNER JOIN Analytics_HourHits ON Analytics_HourHits.HitsStatisticsID = Analytics_Statistics.StatisticsID LEFT JOIN View_CMS_Tree_Joined ON View_CMS_Tree_Joined.NodeID = Analytics_Statistics.StatisticsObjectID AND View_CMS_Tree_Joined.DocumentCulture = Analytics_Statistics.StatisticsObjectCulture WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
SELECT DocumentNamePath AS '{$reports_aggviews_hour.path_header$}', StatisticsObjectCulture AS '{$general.culture$}', Count AS '{$reports_aggviews_hour.hits_header$}', CAST (CAST((100*Count)/(SELECT Counted FROM @countedAll) as decimal(10,2))AS NVARCHAR(10))+'%' as '{$reports_aggviews_hour.hits_percent_header$}' FROM @myselection
